import { useState, useEffect } from "react";
import { Link, useLocation, useParams } from "react-router-dom";


export default function MovieDetails () {
    const params = useParams()
    const key = 'baf3ccdf'
    const [myFilm, setMyFilm] = useState({})
    useEffect(()=> {
        getFilm()
    }, [])

    const getFilm = async () => {
        try{
            const response = await fetch(`https://www.omdbapi.com/?apikey=${key}&i=${params.movieId}`)
            const tempFilm = await response.json()
            setMyFilm(tempFilm)
        }
        catch{console.log('Errore')}
    }


    return(
    <div className="col-xxl-2 col-xl-4 col-md-6 col-sm-6 col-12 position-relative">
        <img className="img-fluid zoomImg" src={myFilm.Poster} alt="" />
     </div>
    )

}
