import { Component } from "react";
import { ListGroup } from "react-bootstrap";

export default class Search extends Component {

    key = "baf3ccdf";
    state = {
      listFilm: [],
      isLoading: true,
    };

    componentDidMount() {

    }

    getFilmFrom = async () => {
      try {
        const response = await fetch(
          `https://www.omdbapi.com/?apikey=${this.key}&s=${this.props.mySearch}`
        );
        const lista = await response.json();
        const arrayTemp = [];
        arrayTemp.push(lista.Search);
        console.log("ArrayTemp", arrayTemp);
        this.setState({
          listFilm: lista.Search,
          isLoading: false
        });
      } catch {console.log('Errore')}
    };

  render() {
        this.getFilmFrom()
        return (

        <ListGroup className=" w-50 position-absolute mt-5 start-50 z-1">

        {this.state.listFilm.map (film => {
            return (
                <ListGroup.Item>{film.Title}</ListGroup.Item>

            )
        })}
        </ListGroup>
     )
  }
}
